import React from 'react';
import { Link, Element, Events, animateScroll as scroll, scrollSpy } from 'react-scroll';
import './Services.css'; 

const Services = () => {
  const scrollToNextSection = () => {
    scroll.scrollTo(700); // Adjust the value based on your content height
  };

  return (
    <div>
      <section className="services-hero">
        <div className="hero-content">
          <h1 className="display-5 fw-bold">Services</h1>
          <Link to="about-section" smooth={true} duration={500}>
            <div className="arrow-down" onClick={scrollToNextSection}></div>
          </Link>
        </div>
      </section>

      <Element name="about-section" className="element">
      <div>
      {/* Full Services Section */}
      <section className="full-services">
        <div className="services-content">
          <h2>Full Services</h2>
          <p>
            Our full services cover everything you need for a complete home cleaning. Whether it's
            your kitchen, bathrooms, or the entire house, we've got you covered. Leave the cleaning
            to us and enjoy a spotless home.
          </p>
        </div>
      </section>

      {/* Special Requests Section */}
      <section className="special-requests">
        <div className="services-content">
          <h2>Special Requests and One-Time Services</h2>
          <p>
            In addition to our full services, we offer special requests and one-time services to
            meet your unique needs. This includes move-in and move-out cleanings, garbage and
            organization services, and add-on services like carpet cleanings, refrigerator, and
            inside the oven cleaning.
          </p>
        </div>
      </section>
    </div>
      </Element>
    </div>
  );
};

export default Services;
